priority_queue<pii, vector<pii>, greater<pii>> pq;


int main() {
  ios_base::sync_with_stdio(0);
  cin.tie(0);
  cin >> n >> s;
  while (n--) {
    int w, p = 0, m = 0; cin >> w;
    for (int i = 0; i < s; i++) {
      int x; cin >> x;
      p <<= 1, m <<= 1;
      if (x == 1) m++;
      else if (x == -1) p++;
    }
    v.push_back(p); adj[p].push_back({m, w});
  }
  memset(dist, 0x3f, sizeof dist);
  pq.push({0, 0});
  while (!pq.empty()) {
    auto [d, u] = pq.top(); pq.pop();
    if (d > dist[u]) continue;
    for (auto [v, w]: adj[u]) if (dist[v] > d + w) {
      dist[v] = d + w;
      pq.push({dist[v], v});
    }
    for (int i = 1; i < (1<<s); i<<=1) {
      int v = u-i;
      if ((i&u) && (dist[v] > d)) {
        dist[v] = d;
        pq.push({dist[v], v});
      }
    }
  }
  for (auto e: v) if (dist[e] != 0x3f3f3f3f) ans = max(dist[e], ans);
  cout << ans;
}

