    char mini = 'z';
    for (int i = 0; i < s.size(); ++i) {
        st.insert(s[i]);
        if (s[i] < mini) mini = s[i];
    }

    string u(st.begin(), st.end());

    if (n < k) {
        t = s;
        t += mini;
    } else {
        map<char, int> dist;
