typedef long long int LL;
int s[1000005];
pair<int,int> sol(int n,int a,int b){
 int g=__gcd(a,b);
 int aa=a, bb=b;
 if(n%g!=0) return MP(-1,-1);
 LL i = 0, j = 1;
 LL i_ = 1, j_ = 0;
 while(1){
  LL q = a / b, r = a % b, t;
        if (r == 0) break;
        a = b; b = r;
        t = i_; i_ = i; i = t - q * i;
        t = j_; j_ = j; j = t - q * j;
    }
   // printf("%d %d %d\n",i,j,g);
    i*=n/g; j*=n/g;
   // printf("%d %d\n",i,j);
    int u;
    if(i<0){
  u=(abs(i)/bb+1);
  i+=u*bb; j-=u*aa;
 }
 else if(j<0){
  u=(abs(j)/aa+1);
  i-=u*bb; j+=u*aa;
 }
 //printf("%d %d\n",i,j);
 if(i>=0 && j>=0) return MP(i,j);
 if(i-bb>=0 && j+aa>=0) return MP(i-bb,j+aa);
 if(i+bb>=0 && j-aa>=0) return MP(i+bb,j-aa);
    return MP(-1,-1);
}
int main(void){
    int n,a,b;
    scanf("%d%d%d",&n,&a,&b);
    pair<int,int> v = sol(n,a,b);
