int n,m;
double fun(int a,vector<vector<bool>> vis,vector<vector<double>> data){
    if(a==0) return 0;
    double minn=1e9+7;
    for(int i=1;i<=n;i++){
        for(int j=1;j<=m;j++){
            if(!vis[i][j]){
                data[i-1][j]+=data[i][j]*0.1;
                data[i][j-1]+=data[i][j]*0.1;
                data[i+1][j]+=data[i][j]*0.1;
                data[i][j+1]+=data[i][j]*0.1;
                data[i-1][j-1]+=data[i][j]*0.1;
                data[i+1][j+1]+=data[i][j]*0.1;
                data[i+1][j-1]+=data[i][j]*0.1;
                data[i-1][j+1]+=data[i][j]*0.1;
                vis[i][j]=true;
                minn= min(fun(a-1,vis,data)+data[i][j],minn);
                data[i-1][j]-=data[i][j]*0.1;
                data[i][j-1]-=data[i][j]*0.1;
                data[i+1][j]-=data[i][j]*0.1;
                data[i][j+1]-=data[i][j]*0.1;
                data[i-1][j-1]-=data[i][j]*0.1;
                data[i+1][j+1]-=data[i][j]*0.1;
                data[i+1][j-1]-=data[i][j]*0.1;
                data[i-1][j+1]-=data[i][j]*0.1;
                vis[i][j]=false;
            }
        }
    }
    return minn;
}
