int last_pending(int a[],int y)
{
    int i = 1;
    while(a[i]!=0)
    {
        if(a[i]-a[i-1]==1&&a[i]%y==1)
        {
            cout << "NO" << endl;
            return 0;
        }
        else if(a[i]-a[i-1]==-1&&a[i-1]%y==1)
        {
            cout << "NO" << endl;
            return 0;
        }
        i++;
    }
    int x = amax;
    cout << "YES" << endl << x << " " << y << endl;
    return 0;
}

