const int N = (int)5e5 + 100;
int t[4 * N], a[N];

int gcd(int a, int b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

void build(int v, int tl, int tr) {
    if (tl == tr)
        t[v] = a[tl];
    else {
        int tm = (tl + tr) / 2;
        build(2 * v, tl, tm);
        build(2 * v + 1, tm + 1, tr);
        t[v] = gcd(t[2 * v], t[2 * v + 1]);
    }
}

void upd(int v, int tl, int tr, int u, int val) {
    if (tl == tr)
        t[v] = val;
    else {
        int tm = (tl + tr) / 2;
        if (u <= tm)
            upd(2 * v, tl, tm, u, val);
        else
            upd(2 * v + 1, tm + 1, tr, u, val);
        t[v] = gcd(t[2 * v], t[2 * v + 1]);
    }
}


bool getgc(int v, int tl, int tr, int l, int r, int x) {
    if (l > r)
        return 1;
    if (tl == l && tr == r)
        return (t[v] % x) == 0;
    int tm = (tl + tr) / 2;
    bool left = getgc(2 * v, tl, tm, l, min(r, tm), x), right = getgc(2 * v + 1, tm + 1, tr, max(tm + 1, l), r, x);
    return left && right;
}

