	cin >> n >> v >> w;
	for(int i=1;i<=n;++i){
		int x;
		cin >> x;
		if(x % 2 == 0) ev[i] += x;
		else od[i] += x;
		ev[i] += ev[i-1];
		od[i] += od[i-1];
	}
	fill(dp, dp+n+1, inf);
	dp[0] = 0;
	for(int l=1;l<=n;++l){
		for(int i=2;l+i-1<=n;i+=2){
			int r = l + i - 1;
			int m = (l + r - 1) / 2;
			if(od[m] - od[l-1] <= v && ev[r] - ev[m] <= w){
				dp[r] = min(dp[r], dp[l-1] + 1);
			}
		}
	}
	if(dp[n] == inf) cout << -1;
	else cout << dp[n];
}

