LL P[10];
struct Node{
 LL p[10];//index 1
 int idx[10];
 int n;
 vector<LL> ans;
 void Init(){
  for (int i = 0; i < 10; ++i)
   idx[i] = 1;
  ans.clear();
 }

 void cale(){
  Init();

  ans.push_back(0);
  ans.push_back(1);
  if (n == 0)
   return;
  for (int i = 2; ans[i-1] < 1e18; ++i){
   int mmin = 1;
   for (int j = 2; j <= n; ++j){
    if (ans[idx[j]] * p[j] < ans[idx[mmin]] * p[mmin])
     mmin = j;
   }
   ans.push_back(ans[idx[mmin]] * p[mmin]);
   LL val = ans[idx[mmin]] * p[mmin];
   for (int j = 1; j <= n; ++j){
    if (ans[idx[j]] * p[j] == val)
     idx[j]++;
   }
  }

 }


}A, B;


void input(){
 in >> n;
 for (int i = 1; i <= n; ++i)
  in >> P[i];
 in >> k;

}

bool judge(LL x){

 LL num = 0;
 int pos = B.ans.size() - 1;
 for (int i = 1; i < A.ans.size(); ++i){
  while (pos > 1 && (log10(A.ans[i]) + log10(B.ans[pos]) > 18 || A.ans[i] * B.ans[pos] > x)){
   pos--;
  }
  if (A.ans[i] * B.ans[pos] <= x)
   num += pos;
 }
 return num >= k;

}


