    B.p[B.n++] = P[i];
   }
  }

  A.n--;
  B.n--;
  A.cale();
  B.cale();

  LL L = 1, R = LL(1e18) + 10;
  LL ans;
  while (L <= R){
   LL mid = (L + R) / 2;
   if (judge(mid)){
    ans = mid;
    R = mid - 1;
   }
   else{
    L = mid + 1;
   }
  }

  cout << ans << endl;










 }


 return 0;
}

