void workNode2(int i,int j,int ip,int jp,int nSteped)
{
    if( i==1 && g_n-1 == j)
        return ;

    int nNewR = 0;
    if( i == ip)
    {
        if( i == 1)
            nNewR = nSteped + nteam2[j];        
        else
            nNewR = nSteped + nteam1[j];        
    }
    else
    {
        nNewR = nSteped + nteam3[j];
    }

    if(  nmidC[i][j] > 0 && nNewR -0 > nmidC[i][j])
            return ;


    std::vector<int> setsNew ;
    setsNew.push_back(nNewR);
    if( nmidA[i][j] > 0)
        setsNew.push_back(nmidA[i][j]);
    if( nmidB[i][j] > 0)
        setsNew.push_back(nmidB[i][j]);
    if( nmidC[i][j] > 0)
        setsNew.push_back(nmidC[i][j]);

    sort(setsNew.begin(),setsNew.end());
    nmidA[i][j] = setsNew[0];
    if( setsNew.size()>1)
        nmidB[i][j] = setsNew[1];
    if( setsNew.size()>2)
        nmidC[i][j] = setsNew[2];

    if( i==0 && j==0)
        return ;

    if( i>0 && j!= jp)
        workNode2(i-1,j,i,j,nNewR);
    if( j>0)
        workNode2(i,j-1,i,j,nNewR);
    //if( i==0 && j!=0 && j!= jp)
    //  workNode2(i+1,j,i,j,nNewR);
}

void work(int n)
{
    g_n = n;
    memset(nmidA,0,sizeof(nmidA));
    memset(nmidB,0,sizeof(nmidB));
    nmidA[1][n-1] = 0;
    nmidB[1][n-1] = 0;

    workNode2(0,n-1,1,n-1,0);
    workNode2(1,n-2,1,n-1,0);
    printf("%d\n",nmidA[0][0] + nmidB[0][0]);
}

