const int maxn = 6e5;
int n,q,seg[maxn];


void upd(int i,int p){
    i+=n;
    seg[i]=p;
    for(i/=2;i>0;i/=2) seg[i]=max(seg[i*2],seg[i*2+1]);
}


int fmax(int l,int r){
    l+=n; r+=n;
    int ans = INT32_MIN;
    while(l<=r){
        if(l&1) ans=max(ans,seg[l++]);
        if(!(r&1)) ans=max(ans,seg[r--]);
        l/=2; r/=2;
    }
    return ans;
}


