main() {
    ios_base::sync_with_stdio(false);
    cin.tie(NULL);
    double k, d, t;
    cin >> k >> d >> t;
    if(k - d < EPS && k - d > -EPS) {
        d *= 2.0;
    }
    if(k - d > EPS) {// k > d;
        ll x = ll(k / d);
        if(double(x) * d - k < EPS && double(x) * d - k > -EPS) {
            cout << t;
            return 0;
        }
        x += 1;
        double tmp = (double(x) * d - k) / 2.0 + k;
        ll l = 0;
        ll r = INF;
        while(r - l > 1) {
            ll mid = (r + l) / 2;
            if(double(mid) * tmp - t > EPS) {
                r = mid;
            } else {
                l = mid;
            }
        }
        double ans = ((double(x) * d - k) + k) * double(l);
        t -= double(l) * tmp;
        double cur = 1.0;
        if(t - k > EPS) {
            t -= k;
            ans += k;
            cur = 0.5;
        }
        double l1 = -EPS, r1 = INF;
        while(r1 - l1 > EPS) {
            double mid = (r1 + l1) / 2;
            if(cur * mid - t > EPS) {
                r1 = mid;
            } else {
                l1 = mid;
            }
        }
        ans += l1;
        cout << fixed << setprecision(10) << ans;
    } else {
        double tmp = k + (d - k) / 2.0;
        ll l = 0;
        ll r = INF;
        while(r - l > 1) {
            ll mid = (r + l) / 2;
            if(double(mid) * tmp - t > EPS) {
                r = mid;
            } else {
                l = mid;
            }
        }
        double ans = d * double(l);
        t -= double(l) * tmp;
        double cur = 1.0;
        if(t - k > -EPS) {
            t -= k;
            ans += k;
            cur = 0.5;
        }
        double l1 = -EPS, r1 = INF;
        while(r1 - l1 > EPS) {
            double mid = (r1 + l1) / 2;
            if(cur * mid - t > EPS) {
                r1 = mid;
            } else {
                l1 = mid;
            }
        }
        ans += l1;
        cout << fixed << setprecision(10) << ans;
    }
}

