const int MAX_LEN = 100005;

int bcc [2][MAX_LEN], ac [2][MAX_LEN];

int get_bcc (int idx, int l, int r) {
  return bcc[idx][r] - bcc[idx][l - 1];
}

int get_ac (int idx, int l, int r) {
  if (ac[idx][r] >= r - l) {
    return r - l;
  } else {
    return ac[idx][r];
  }
}

int main () {
