  string S, T;
  cin >> S >> T;

  int queryc;
  cin >> queryc;

  for (int i = 1; i <= (int) S.size(); i++) {
    if (S[i - 1] == 'B' || S[i - 1] == 'C') {
      bcc[0][i]++;
    } else {
      ac[0][i]++;
    }
  }

  for (int i = 1; i <= (int) T.size(); i++) {
    if (T[i - 1] == 'B' || T[i - 1] == 'C') {
      bcc[1][i]++;
    } else {
      ac[1][i]++;
    }
  }

  for (int i = 1; i <= (int) S.size(); i++) {
    bcc[0][i] += bcc[0][i - 1];
    if (ac[0][i] != 0) {
      ac[0][i] += ac[0][i - 1];
    }
  }

  for (int i = 1; i <= (int) T.size(); i++) {
    bcc[1][i] += bcc[1][i - 1];
    if (ac[1][i] != 0) {
      ac[1][i] += ac[1][i - 1];
    }
  }

  for (int i = 0; i < queryc; i++) {
    int sl, sr, tl, tr;
    cin >> sl >> sr >> tl >> tr;

    bool ok = true;
    ok &= (get_bcc(0, sl, sr) % 2) == (get_bcc(1, tl, tr) % 2);
    ok &= get_bcc(0, sl, sr) <= get_bcc(1, tl, tr);
    ok &= get_ac(0, sl, sr) >= get_ac(1, tl, tr);

    cout << ok;
  }
  cout << endl;
}

