vector<pair<int,ll>> g[100007];
ll distS[100007],distE[100007],dist[100007];
bool vis[100007];
const ll INF=1e18+7;
priority_queue<pair<ll,int>,vector<pair<ll,int>>,greater<pair<ll,int>>> pq;


int main()
{
    cin.tie(nullptr)->ios::sync_with_stdio(false);


    int n,m,A,B,C,D;
    cin>>n>>m;
    for(int i=0;i<m;i++)
    {
        int u,v,w;
        cin>>u>>v>>w;
        g[u].emplace_back(v,w);
        g[v].emplace_back(u,w);
    }
    cin>>A>>B>>C>>D;
    for(int i=1;i<=n;i++)
        distS[i]=distE[i]=dist[i]=INF;


    pq.emplace(distS[A]=0,A);
    while(!pq.empty())
    {
        auto [dis,u]=pq.top();
        pq.pop();
        vis[u]=true;
        for(auto [v,w]:g[u])
        {
            if(vis[v])continue;
            if(distS[v]>dis+w)
            {
                distS[v]=dis+w;
                pq.emplace(distS[v],v);
            }
        }
    }
    pq.emplace(distE[B]=0,B);
    memset(vis,false,sizeof(vis));
    while(!pq.empty())
    {
        auto [dis,u]=pq.top();
        pq.pop();
        vis[u]=true;
        for(auto [v,w]:g[u])
        {
            if(vis[v])continue;
            if(distE[v]>dis+w)
            {
                distE[v]=dis+w;
                pq.emplace(distE[v],v);
            }
        }
    }


    memset(vis,false,sizeof(vis));
    for(int i=1;i<=n;i++)
        if(distS[i]+distE[i]==distS[B])
            vis[i]=true;


    pq.emplace(dist[C]=0,C);
    while(!pq.empty())
    {
        auto [dis,u]=pq.top();
        pq.pop();
        vis[u]=true;
        for(auto [v,w]:g[u])
        {
            if(vis[v])continue;
            if(dist[v]>dis+w)
            {
                dist[v]=dis+w;
                pq.emplace(dist[v],v);
            }
        }
    }
    if(dist[D]==INF)cout<<"-1";
    else cout<<dist[D];
    return 0;
}

