double med(vector<double> v) {
    if(v.size()%2 == 0) {
        return (v[v.size()/2]+v[v.size()/2-1])/2.0;
    } else {
        return v[v.size()/2];
    }
}


void dfs(char u) {
    char next;
    visited[u] = true;
    path.push_back(u);
    for(auto i : v[u]) {
