    for(int i=0;i<n;i++){
        cin>>e[i];
    }
    ll l=0,r=2e9;
    ll ans=0; //500000*4000
    while(l<=r){
        ll mid=(l+r)/2;
        int ct=0;
        ll sum=0;
        multiset<int> ms;
        for(int i=0;i<n;i++){
            sum+=e[i];
            ms.insert(e[i]);
