                sum-=(*ms.begin());
                ms.erase(ms.begin());
            }
            if(ms.size()==k && sum>=mid){
                ct++;
                while(!ms.empty()) ms.erase(ms.begin());
                sum=0;
            }
        }
        if(ct>=m){
            l=mid+1;
            ans=max(ans,(ll)mid);
        }
        else r=mid-1;
    }
    cout<<ans;
}

