long long dp(int i, int k){
    if(i == N) return 0;
    if(memo[i][k] != -1) return memo[i][k];
    memo[i][k] = 0;
    long long prod = v[i];
    for(int t = 0; t <= k; t++){

        memo[i][k] = max(memo[i][k], prod | dp(i+1, k - t));
        prod *= X;
    }
    return memo[i][k];
}
