const int INF = 1e9;
const int MAXN = 300000, MAXLOG = 20;

struct Tree{
    int n;
    vector<int> tree[MAXN];

    int prev[MAXN];
    int rank[MAXN];
    int getPrev(int v){
        if (v == prev[v])
            return v;
        prev[v] = getPrev(prev[v]);
        return prev[v];
    }
    void join(int a, int b){
        a = getPrev(a);
        b = getPrev(b);
        if (rank[a] < rank[b])
            swap(a, b);
        prev[b] = a;
        if (rank[a] == rank[b])
            rank[a]++;
    }

    Tree(vector<pair<int, int> > s[], int nn){
        n = nn;
        for (int i = 0; i < n; i++){
            prev[i] = i;
            rank[i] = 0;
        }
        for (int i = 0; i < n; i++){
            for (pair<int, int> u : s[i]){
                if (u.second == 0)
                    join(i, u.first);
            }
        }
        set<int> lst[MAXN];
        for (int i = 0; i < n; i++){
            for (pair<int, int> u : s[i]){
                if (getPrev(u.first) != getPrev(i))
                    lst[getPrev(i)].insert(getPrev(u.first));
            }
        }
        for (int i = 0; i < n; i++){
            for (auto u : lst[i])
                tree[i].push_back(u);
        }
    }

    int h[MAXN], tin[MAXN], color[MAXN];
    vector<int> euler;
    void dfs(int v){
        color[v] = 1;
        tin[v] = euler.size();
        euler.push_back(v);
        for (int u : tree[v]){
            if (color[u] == 0){
                h[u] = h[v] + 1;
                dfs(u);
                euler.push_back(v);
            }
        }
    }

    int sparse[MAXLOG][MAXN], power[MAXN + 1];
    void precalc(){
        for (int i = 0; i < n; i++){
            if (color[i] == 0 && getPrev(i) == i){
                dfs(i);
            }
        }
        int m = euler.size();
        int k = 1;
        int b = 1;
        while (b < m){
            b *= 2;
            k++;
        }
        for (int i = 0; i < m; i++)
            sparse[0][i] = euler[i];
        for (int i = 0; i < k - 1; i++){
            for (int j = 0; j < m; j++){
                sparse[i + 1][j] = sparse[i][j];
                if (j + (1 << i) < m && tin[sparse[i][j]] > tin[sparse[i][j + (1 << i)]])
                    sparse[i + 1][j] = sparse[i][j + (1 << i)];
            }
        }
        int last = 1;
        power[1] = 0;
        for (int i = 2; i < m + 1; i++){
            if (2 * last == i){
                power[i] = power[i - 1] + 1;
                last = i;
            }
            else
                power[i] = power[i - 1];
        }
    }
    int get(int v, int u){
        v = getPrev(v);
        u = getPrev(u);
        if (tin[v] > tin[u])
            swap(v, u);
        int len = tin[u] - tin[v] + 1;
        int p1 = sparse[power[len]][tin[v]];
        int p2 = sparse[power[len]][tin[u] - (1 << power[len]) + 1];
        if (tin[p2] < tin[p1])
            p1 = p2;
        return h[v] + h[u] - 2 * h[p1];
    }

};


int dist[MAXN];
vector<int> q;
vector<int> s[MAXN];

void bfs(vector<int> lst){
    //print(lst);
    q.clear();
    for (int v : lst){
        dist[v] = 0;
        q.push_back(v);
    }
    int cur = 0;
    while (cur < q.size()){
        int v = q[cur];
        cur++;
        for (int u : s[v]){
            if (dist[u] > dist[v] + 1){
                dist[u] = dist[v] + 1;
                q.push_back(u);
            }
        }
    }
}


