  cntG[a[i]]++;
 }

 power[0] = 1;

 for(int i = 1; i <= n; i++) {
  power[i] = add(power[i - 1], power[i - 1]);
 }

 for(int i = K - 1; i >= 2; i--) {
  int new_c = cntG[i];
  lp[i] = i;
  for(int j = i + i; j < K; j += i) {
   new_c += new_c;
   ans[i] = add(ans[i], mod - ans[j]);
   lp[j] = i;
  }
  cnt[i] = new_c;
  ans[i] = add(ans[i], add(power[new_c], mod - 1));
 }

 int rans = 0;

 for(int i = 2; i < K; i++) {
  if(ans[i])
   rans = add(rans, mul(ans[i], get(i)));
 }

 printf("%d\n", rans);

 return 0;

}

