const int N = 1e5+10;


struct info{
    int u, idx;
    info(int _u, int _idx){
        u = _u;
        idx = _idx;
    }
};
int in[N], com[N];
ll ans[N];
vector<info> adj[N];
int main(){
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);
    int n, m;
    cin >> n >> m;
    for(int i = 1; i <= n; ++i){
        int u, v;
        cin >> u >> v;
        adj[u].push_back(info(v, i));
        adj[v].push_back(info(u, i));
        in[u]++;
        in[v]++;
    }
    queue<int> q;
    for(int i = 1; i <= n; ++i){
        com[i] = 1;
        if(in[i] == 1) q.push(i);
    }
    while(!q.empty()){
        int u = q.front(); q.pop();
        for(auto [v, idx] : adj[u]){
            if(in[v] == 0) continue;
            ans[idx] = 1LL*(n-com[u])*com[u];
            com[v] += com[u];
            --in[u];
            if(--in[v] == 1) q.push(v);
        }
    }
    for(int i = 1; i <= m; ++i) cout << ans[i] << ' ';
}

