    st.push(node);
}
void dfs_scc2(int node, int group) {
    if(vis_scc[node])return;
    vis_scc[node] = true;
    gr[node] = group;
    for(int i:rev_g[node]) {
        if(!vis_scc[i]) dfs_scc2(i, group);
    }
}
void dfsy(int node) {
    if(visited[node]==1) return;
    visited[node] = 1;
    if(gr_size[gr[node]]>1)reach_draw = true;
    for(int i:g[node]) {
        if(!visited[i]) {
            dfsy(i);
            if(gr_size[gr[i]] > 1) reach_draw = true;
        }
    }
}

