            cin>>v;
            g[i].pb(v);
            rev_g[v].pb(i);
        }
    }
    int s,ss;
    cin>>s;
    ss = s;
    dfsx(s);

    if(reach[s][0]) {
        cout<<"Win\n";

        bool cc = false;
        while(next_n[s][cc] != -1) {
            cout<<s<<" ";
            s = next_n[s][cc];
            cc = !cc;
        }
        cout<<s<<"\n";
    }
    else {
        for(int i=1;i<=n;i++) {
            if(!vis_scc[i]) dfs_scc1(i);
        }

        memset(vis_scc,false,sizeof(vis_scc));
        int br = 1;
        while(!st.empty()) {
            int curr = st.top();
            st.pop();
            if(!vis_scc[curr]) {
                dfs_scc2(curr,br);
                br++;
            }
        }
