    while (tc--)
    {
        for (int i = 1; i <= SIZE_Y; i++)
        {
            for (int j = 1; j <= SIZE_X; j++)
            {
                d[i][j] = false;
            }
        }

        int size_x, m;
        scanf("%d%d", &size_x, &m);

        int start_y = 0, start_x = 0;
        for (int i = 1; i <= SIZE_Y; i++)
        {
            scanf("%s", table[i] + 1);

            for (int j = 1; j <= size_x; j++)
            {
                if (table[i][j] == 's')
                {
                    start_y = i;
                    start_x = j;
                    break;
                }
            }
        }
        d[start_y][start_x] = true;

        for (int x = 4; x <= size_x; x++)
        {
            for (int y = 1; y <= SIZE_Y; y++)
            {
                if (table[y][x] != '.') continue;

                for (int k = -1; k <= 1; k++)
                {
                    d[y][x] |= (d[y + k][x - 3] & (table[y + k][x - 2] == '.' && table[y][x - 2] == '.' && table[y][x - 1] == '.'));
                }
            }
