            //segment of form <kx,0> --> <(k+1)x,x>
            // formula for segment: y = x - left_bound
            x_proj = a - left_bound;
        }
        else {
            // segment of form <kx,x> -->  <(k+1)x, 0>
            // formulat for segment: y = right_bound -x
            x_proj = right_bound - a;
        }
        double diff = b - x_proj;
        if ((num_steps-1)*b == a)
            ans += diff / (num_steps - 1);
        else
            ans += diff / num_steps;
        cout << std::fixed << std::setprecision(12) << ans << '\n';
    }

    //print_timer();
    return 0;
}

