long long sub_matrix(int end , int start ,int ones, int order)
{
    if(end>=start)
    {
        int mid=end/2;
    long long sorder=order/mid;
    long long no_of_ones=(order*order)-(sorder*sorder);
    if(no_of_ones==ones)
        return mid;

    if(no_of_ones>ones)
        sub_matrix(mid-1 , start , ones , order);

        sub_matrix(end , mid+1 , ones , order);
    }



    return -1;
}


