        scanf("%d %d", &x, &y);
        if ((u[x]+1)%h == u[y]) {
            g[x].push_back(y);
        }
        if ((u[y]+1)%h == u[x]) {
            g[y].push_back(x);
        }
    }
    Saber.Excalibur(n);
}

