inline ll pwr(ll base,ll n){ll ans=1;while(n>0){ if(n%2==1)ans=ans*base;  base=base*base; n/=2;} return ans;}
struct range{int l,h;};
using namespace std;
struct trie
{
    int bit[2];
    trie *next[2];
};
int pp=pwr(2,30);
int aa[300001]={0};
trie* newnode()
{
 trie *h=new trie;
 h->bit[0]=h->bit[1]=0;
 h->next[0]=h->next[1]=NULL;
 return h;
}
void insert(trie* h,int a,int j)
{
    if(j<0)return;
    if(a&(1<<j))
    {

        h->bit[1]++;
        if(h->next[1]==NULL)
        {
         h->next[1]=newnode();

        }
        insert(h->next[1],a,j-1);
    }
    else
    {

        h->bit[0]++;
        if(h->next[0]==NULL)
        {
         h->next[0]=newnode();
        }
        insert(h->next[0],a,j-1);
    }
}
void update(trie* h,int a,int j)
{
    if(j<0)return;
    if(a&(1<<j))
    {
        h->bit[1]--;
        update(h->next[1],a,j-1);
    }
    else
    {
        h->bit[0]--;
        update(h->next[0],a,j-1);
    }
}

int ans[300000];
void find(trie *h,int a,int j,int v,int i)
{
    if(j<0)
    { 
        if(v<ans[i])ans[i]=v;
        return;
    }
    int s=a&(1<<j);
    if(s)
    {

        if(h->bit[1]>0)
            {
                find(h->next[1],a,j-1,v,i);
            }
        else
            {
                v+=pwr(2,j);
                find(h->next[0],a,j-1,v,i);
            }
    }
    else
    {
        if(h->bit[0]>0)
            {
                find(h->next[0],a,j-1,v,i);
            }
        else
            {
                v+=pwr(2,j);
                find(h->next[1],a,j-1,v,i);
            }
    }
}

