   bool c = (wchar_t)toupper((*i)[j]) == (wchar_t)toupper(s[j]) && 
         (wchar_t)tolower((*i)[j]) == (wchar_t)tolower(s[j]);
   if(a || b || c)
   {
    if(j == s.size() - 1)
    {
     std::cout << *i << std::endl;
     return false;
    }
    continue;
   }
   else break;
  }
  //std::cout << std::endl;
 }

