bool isletter(char a)
{
    if(a>='A'&&a<='Z')
        return true;
    else if(a>='a'||a<='z')
        return true;
    else
        return false;
}
bool issame(char a,char b)
{
    if(a>'Z')
    {
        if(b>'Z')
        {
            if(a==b)
                return true;
            else
                return false;
        }
        else
        {
            if(a==b-'A'+'a')
                return true;
            else
                return false;
        }
    }
    else
    {
        if(b>'Z')
        {
            if(a==b-'a'+'A')
                return true;
            else
                return false;
        }
        else
        {
            if(a==b)
                return true;
            else
                return false;
        }
    }
}
bool issame2(char a[],char b[])
{
    if(strlen(a)!=strlen(b))
        return false;
    for(int i=0;i<strlen(a);i++)
    {
        if(isletter(a[i]))
        {
            if(!issame(a[i],b[i]))
                return false;
        }
        else
        {
            if(a[i]!=b[i])
                return false;
        }
    }
    return true;
}
bool spcompare(char a[],char b[])
{
    char a1[100],b1[100];
    int num1=0,num2=0;
    for(int i=0;i<strlen(a);i++)
    {
        if(a[i]=='+')
            break;
        else if(a[i]=='.')
            continue;
        else
        {
            if(a[i]>='A'&&a[i]<='Z')
                a1[num1++]=a[i]-'A'+'a';
            else
                a1[num1++]=a[i];
        }
    }
    a1[num1]='\0';
    for(int i=0;i<strlen(b);i++)
    {
        if(b[i]=='+')
            break;
        else if(b[i]=='.')
            continue;
        else
        {
            if(b[i]>='A'&&b[i]<='Z')
                b1[num2++]=b[i]-'A'+'a';
            else
                b1[num2++]=b[i];
        }
    }
    b1[num2]='\0';
    if(strcmp(a1,b1)==0)
        return true;
    else
        return false;
}
bool compare(char a[],char b[])
{
    char a1[100],b1[100];
    int num1=0,num2=0;
    for(int i=0;i<strlen(a);i++)
    {
        if(a[i]>='A'&&a[i]<='Z')
            a1[num1++]=a[i]-'A'+'a';
        else
            a1[num1++]=a[i];
    }
    a1[num1]='\0';
    for(int i=0;i<strlen(b);i++)
    {
        if(b[i]>='A'&&b[i]<='Z')
            b1[num2++]=b[i]-'A'+'a';
        else
            b1[num2++]=b[i];
    }
    b1[num2]='\0';
    if(strcmp(a1,b1)==0)
        return true;
    else
        return false;
}
int main(int argc, char* argv[])
{
    int num=0,nums=0;
    scanf("%d",&n);
    char a[100];
    for(int i=0;i<n;i++)
    {
        scanf(" %s",&a);
        int j,k;
        for(j=0;j<strlen(a)&&a[j]!='@';j++)
            address[i].login[j]=a[j];
