    for(int i=0;i<n;i++)
    {
        if(vis[i]) continue;
        strcpy(ans[num].login,address[i].login);
        strcpy(ans[num].domain,address[i].domain);
        vis[i]=1;
        num++;
        bool flag=false;
        if(issame2(address[i].domain,sp))
            flag=true;
        for(int j=i+1;j<n;j++)
        {
            if(vis[j]) continue;
            if(flag)
            {
                if(!issame2(address[j].domain,sp))
                    continue;
                if(spcompare(address[i].login,address[j].login))
                {
                    ans[num]=address[j];
                    vis[j]=1;
                    num++;
                }
            }
            else
            {
                if(!issame2(address[j].domain,address[i].domain))
                    continue;
                if(compare(address[i].login,address[j].login))
                {
                    ans[num]=address[j];
                    vis[j]=1;
                    num++;
                }
            }
        }
        ret[nums]=num;
        nums++;
    }
    printf("%d\n",nums);
    int j=0;
    for(int i=0;i<nums;i++)
    {
        if(i)
            printf("%d",ret[i]-ret[i-1]);
        else
            printf("%d",ret[i]);
        for( ;j<ret[i];j++)
            printf(" %s@%s",&ans[j].login,&ans[j].domain);
        if(i!=nums-1)
            printf("\n");
    }
    return 0;
}

