    double rad= r , x_c= x_cc , y_c= y_cc , x_l = x_ll , y_l = y_ll ;
    double Sum = getDis(x_c, y_c , x_l ,y_l) ;
    if( Sum - rad > eps){
        printf("%.15f %.15f %.15f\n",x_c, y_c,rad);
        return 0 ;
    }
    Sum=Sum + rad;
    for( double i = 0.0 ;i<=360.0 ;i=i + 0.001 ){
        double deg = (i*pii)/180.0 ;
        double x_i = rad*cos(deg) + x_c ;
        double y_i = rad*sin(deg) + y_c ;
        double dis = getDis(x_i, y_i , x_l , y_l) ;
        if( fabs(dis - Sum)<= eps){
            printf("%.15f %.15f %.15f\n",(x_i + x_l)/2.0 , (y_i + y_l)/2.0 , Sum/2.0);
            return 0;
        }
    }
    return 0;
}

