#include <bits/stdc++.h>
using namespace std;
int n,m;
int connected[200005];
vector<int> edgeList[200005];
map<pair<int,int>,bool> badedges;
bool visited[200005];
int parent[200005];
int findParent(int v)
{
    if(parent[v] == v)
        return v;
    return parent[v] = findParent(parent[v]);
}
int higheN()
{
    int highestConnection = -1e8;
    int pt = -1;
    for(int i = 1;i <= n;i++)
    {
        if(visited[i])
            continue;
        if(connected[i] > highestConnection)
        {
            highestConnection = connected[i];
            pt = i;
        }
    }
    return pt;
}
int ans[200005];
int main()
{
    cin >> n >> m;
    for(int i = 1;i <= n;i++)
        connected[i] = n-1,parent[i] = i;

    for(int i = 0;i < m;i++)
    {
        int u,v;
        scanf("%d %d",&u,&v);
        edgeList[u].push_back(v);
        edgeList[v].push_back(u);
        badedges[{u,v}] = badedges[{v,u}] = true;
        connected[u]--;
        connected[v]--;
    }
    while(true)
    {
        int curVertex = higheN();
        if(curVertex == -1)
            break;
        visited[curVertex] = true;
        for(int i = 1;i <= n;i++)
        {
            if(i == curVertex || badedges[{curVertex,i}] || badedges[{i,curVertex}])
                continue;
            visited[i] = true;
            int u = findParent(i);
            int v = findParent(curVertex);
            if(u == v)
                continue;
            parent[u] = v;
        }
    }
    for(int i = 1;i <= n;i++)
    {
        ans[parent[i]]++;
    }
    vector<int> ff;
    for(int i = 1;i <= n;i++)
    {
        if(ans[i] > 0)
            ff.push_back(ans[i]);
    }
    sort(ff.begin(),ff.end());

    cout << ff.size() << endl;
    for(int i = 0;i < ff.size();i++)
        printf("%d ",ff[i]);
}

#include <bits/stdc++.h>
using namespace std;


typedef long long ll;
typedef pair<ll,ll> pll;


ll score = 0;


vector<pll> del;
vector<ll> dx = {0, 0, -1, 1};
vector<ll> dy = {-1, 1, 0, 0};


void check(ll y, ll x, char pat, vector<vector<char>>& table, vector<vector<bool>>& visited, ll m, ll n){
    visited[y][x] = true;
    del.push_back({y, x});
    score += 5;


    queue<pll> q;
    q.push({y, x});


    while(!q.empty()){
        auto [cy, cx] = q.front();
        q.pop();


        for(ll i = 0; i < 4; i++){
            ll new_x = dx[i] + cx;
            ll new_y = dy[i] + cy;
            if(new_x >= 0 && new_x < n && new_y >= 0 && new_y < m){
                if(table[new_y][new_x] == pat && !visited[new_y][new_x]){
                    visited[new_y][new_x] = true;
                    q.push({new_y, new_x});
                    del.push_back({new_y, new_x});
                    score += 5;
                }
            }
        }
    }
}


void apply_gravity(vector<vector<char>>& table, ll m, ll n){
    for (ll j = 0; j < n; j++) {
        for (ll i = m - 2; i >= 0; i--) {
            if (table[i][j] != '-' && table[i][j] != '#') {
                ll temp_i = i;
                while (temp_i + 1 < m && table[temp_i + 1][j] == '-') {
                    swap(table[temp_i][j], table[temp_i + 1][j]);
                    temp_i++;
                }
            }
        }
    }
}


int main() {
    ios_base::sync_with_stdio(false);
    cin.tie(nullptr);


    ll m, n;
    cin>> m>> n;


    vector<vector<char>> table(m, vector<char>(n));
    vector<vector<bool>> visited(m, vector<bool>(n, false));


    for(ll i = 0; i < m; i++){
        for(ll j = 0; j < n; j++){
            cin>> table[i][j];
        }
    }


    ll many;
    cin>> many;


    while(many--){
        ll y, x;
        char c;
        cin>> y>> x>> c;


        if(table[y][x] == '#' || table[y][x] == '-'){
            score -= 5;
            continue;
        }


        ll move_x = (c == 'L') ? -1 : 1;
        ll new_x = x + move_x;


        if(new_x < 0 || new_x >= n || table[y][new_x] != '-'){
            score -= 5;
            continue;
        }


        char pat = table[y][x];
        table[y][x] = '-';


        while(y + 1 < m && table[y + 1][new_x] == '-'){
            y++;
        }


        table[y][new_x] = pat;
        check(y, new_x, pat, table, visited, m, n);


        if(del.size() == 1){
            score -= 5;
            auto [py, px] = del[0];
            table[py][px] = pat;
            visited[py][px] = false;
            del.clear();
            continue;
        }


        for(auto& [py, px] : del){
            table[py][px] = '-';
            visited[py][px] = false;
        }
        del.clear();


        apply_gravity(table, m, n);
    }


    cout<< score<< "\n";
    for(ll i = 0; i < m; i++){
        for(ll j = 0; j < n; j++){
            cout<< table[i][j] << " ";
        }
        cout<< "\n";
    }


    return 0;
}

