#define ld long double
ll L, R;
ll dp[100];
bool vis[100];

ll bin( ll power, ll obj )
{
    ll lo = 1, hi = R, mid, p = 1;
    while( lo <= hi ) {
        mid = (lo + hi)/2;
        bool ok = 0;
        p = 1;
        for( int i = 1; i <= power; ++i ) {
            if( p > (ld)obj/(ld)mid ) {
                ok = 1;
                break;
            }
            p *= mid;
        }
        if( ok ) {
            hi = mid-1;
        }
        else lo = mid+1;
    }
    return hi;
}

void recur( int pos, int cur )
{
    if( pos == 19 ) return;
    if( cur > 60 ) return;
    recur( pos+1, cur );
    recur( pos+1, cur*prime[pos] );
    //dbg(cur);
    if( cur != 1 && !vis[cur] && lower_bound( prime.begin(), prime.end(), cur ) == upper_bound( prime.begin(), prime.end(), cur ) ) {
        int up = bin( cur, R );
        if( up >= 1 ) --up;
        dp[cur] += up;
        int lo = bin( cur, L-1 );
        if( lo >= 1 ) --lo;
        dp[cur] -= lo;
        vis[cur] = 1;
        //dbg(up-lo, cur);
    }
}

