long long n, k, x, ans;
vector<long long> in;

void solve(int idx, int have) {
    if (idx == k || !have) {
        long long hope = 0;
        for (int i = 0; i < n; i++)
            hope |= in[i];
        ans = max(ans, hope);
        return;
    }
    solve(idx + 1, have);
    while (have) {
        in[idx] *= x;
        solve(idx, have - 1);
        in[idx] /= x;
        have--;
    }
}

