ll n,s,t,m,q;
int main(){
	ios_base::sync_with_stdio(false);
	cin.tie(NULL);
	cin >> n >> s >> t >> m;
	for(int i=0;i<m;i++){
		ll u,v,w;
		cin >> u >> v >> w;
		adj[u].push_back({v,w});
	}
	for(int i=0;i<n;i++){
		for(int j=0;j<n;j++){
			dist[i][j] = LLONG_MAX;
			dp2[i][j] = LLONG_MAX;
		}
	}
	for(int i=0;i<n;i++){	
		dp[i] = {LLONG_MAX,LLONG_MAX};
	}
	minh = {LLONG_MAX,LLONG_MAX};
	dist[s][0] = 0;
	pq.push({s,0,0});
	while(!pq.empty()){
		ll u = pq.top().u;
		ll w = pq.top().w;
		ll h = pq.top().h;
		pq.pop();
		if(w >= minh.st && h > minh.nd) continue;
		if(u == t){
			if(dp[h].st > w){
				dp[h].st = w;
				p.insert({h,w});
				minh.st = min(minh.st,w);
				minh.nd = min(minh.nd,h);
			}
			continue;
		}
		for(auto x : adj[u]){
			ll v = x.st;
			ll wg = x.nd;
			if(dist[v][h+1] > dist[u][h] + wg){
				dist[v][h+1] = dist[u][h] + wg;
				if(dp2[v][h+1] > dist[v][h+1]) pq.push({v,dist[v][h+1],h+1}),dp2[v][h+1] = dist[v][h+1];
				else continue;
				//pq.push({v,dist[v][h+1],h+1});
			}
		}
	}
