	for(int i=0;i<q;i++){
		ll k;
		cin >> k;
		ll ans = LLONG_MAX;
		for(auto x : p){
			ll h = x.st;
			ll w = x.nd;
			ans = min(ans,w + (k * (h - 1)));
		}
		cout << ans << " ";
	}
}

