#define all(x) x.begin(), x.end()
#define rizz ios_base::sync_with_stdio(false); cin.tie(nullptr);
#define yap cout
#define int long long
using namespace std;
using namespace __gnu_pbds;
template <class T>
using pbds = tree<T, null_type, less<T>, rb_tree_tag, tree_order_statistics_node_update>;


const int MAXN = 200100;
vector<int> vec(MAXN);
int dp1[MAXN], dp2[MAXN];
int seg[4 * MAXN];
int n, q;


void update(int node, int l, int r, int idx, int val) {
    if (l == r) {
        seg[node] = max(seg[node], val);
        return;
    }
    int mid = (l + r) >> 1;
    if (idx <= mid) update(node * 2, l, mid, idx, val);
    else update(node * 2 + 1, mid + 1, r, idx, val);
    seg[node] = max(seg[node * 2], seg[node * 2 + 1]);
}


int query(int node, int l, int r, int ql, int qr) {
    if (qr < l || ql > r) return 0;
    if (ql <= l && r <= qr) return seg[node];
    int mid = (l + r) >> 1;
    return max(
        query(node * 2, l, mid, ql, qr),
        query(node * 2 + 1, mid + 1, r, ql, qr)
    );
}


int32_t main() {
    rizz;
    cin >> n >> q;
    for (int i = 0; i < n; ++i) cin >> vec[i];
    vector<int> vals(vec.begin(), vec.begin() + n);
    sort(all(vals));
    vals.erase(unique(all(vals)), vals.end());
    int M = vals.size();
    unordered_map<int, int> cord;
    cord.reserve(M * 2);
    for (int i = 0; i < M; ++i) cord[vals[i]] = i + 1;
    fill(seg, seg + 4 * MAXN, 0);
    for (int i = 0; i < n; ++i) {
        int idx = cord[vec[i]];
        int best = (idx > 1 ? query(1, 1, M, 1, idx - 1) : 0);
        dp1[i] = best + 1;
        update(1, 1, M, idx, dp1[i]);
    }


    //for (int i = 0; i < n; ++i) yap << dp1[i] << ' ';
    //yap << '\n';
    fill(seg, seg + 4 * MAXN, 0);
    for (int i = n - 1; i >= 0; --i) {
        int idx = cord[vec[i]];
        int best = (idx > 1 ? query(1, 1, M, 1, idx - 1) : 0);
        dp2[i] = best + 1;
        update(1, 1, M, idx, dp2[i]);
    }


    while (q--) {
        int a;
        cin >> a;
        yap << min(dp1[a], dp2[a])-1 << '\n';
    }


    return 0;
}

