#include<bits/stdc++.h>
#define f first
#define s second
#define ll long long
#define pb push_back
#define pii pair<int,int>
#define pll pair<ll,ll>
#define sz(x) (int)x.size()
#define all(x) x.begin(),x.end()
using namespace std;
vector<pll>p,tmp,tmp2;
pll tr;
ll area(pll O, pll P, pll Q) {
	return (P.first - O.first) * (Q.second - O.second) -
	       (P.second - O.second) * (Q.first - O.first);
}
ll cross(pll a,pll b){
    return a.f*b.s-b.f*a.s;
}
ll lexcmp(pll l,pll r){
    return l.f<r.f||(l.f==r.f&&l.s<r.s);
}
bool ch(pll a,pll b,pll c,pll x){
    ll s1=abs(area(a,b,c));
    ll s2=abs(area(x,a,b))+abs(area(x,b,c))+abs(area(x,c,a));
    return s1==s2;
}
void solve(){
    sort(all(p));
    vector<pll>hull;
    p.erase(unique(all(p)),p.end());
    int n=sz(p);
    for(int i=0;i<n;i++){
        while(sz(hull)>1&&area(hull[sz(hull)-2],hull.back(),p[i])<=0)hull.pop_back();
        hull.pb(p[i]);
    }int low=sz(hull);
    for(int i=n-2;i>=0;i--){
        while(hull.size()>low&&area(hull[sz(hull)-2],hull.back(),p[i])<=0)hull.pop_back();
        hull.pb(p[i]);
    }hull.pop_back();
    p=hull;
}
void build(){
    int n=p.size();
    int pos = 0;
    for(int i=1;i<n;i++){
        if(p[i]<p[pos])pos=i;
    }
    rotate(p.begin(),p.begin()+pos,p.end());
    n--;
    vector<pll>sq;
    for(int i=0;i<n;i++)sq.pb({p[i+1].f-p[0].f,p[i+1].s-p[0].s});
    tr=p[0];
    p=sq;
}
int sign(ll v){
    if(v==0)return 0;
    return v>0?1:-1;
}
bool qr(pll x){
    x = {x.f-tr.f,x.s-tr.s};
    if(cross(p[0],x)<=0&&sign(cross(p[0],x))!=sign(cross(p[0],p.back())))return 0;
    if(cross(p.back(),x)!=0&&sign(cross(p.back(),x))!=sign(cross(p.back(),p[0])))return 0;
    if(cross(p[0],x)==0)return p[0].f*p[0].f+p[0].s*p[0].s>=x.f*x.f+x.s*x.s;
    int l=0,r=p.size()-1;
    while(r-l>1){
        int m=(l+r)/2;
        if(cross(p[m],x)>=0)l=m;
        else r=m;
    }return ch(p[l],p[l+1],{0,0},x);
}
int main(){
    ios_base::sync_with_stdio(0);cin.tie(0);
    int n,m;cin>>n>>m;
    for(int i=0;i<n;i++){
        ll x,y;cin>>x>>y;
        tmp.pb({x,y});
    }p=tmp;
    solve();
    build();
    ll ans=0;
    for(int i=0;i<m;i++){
        ll x,y;cin>>x>>y;
        tmp2.pb({x,y});
        if(qr({x,y}))ans++;
    }p=tmp2;
    solve();
    build();
    for(auto it : tmp){
        if(qr(it))ans++;
    }cout<<ans;
}

