struct VISUKITUPIE{
 pair<int, int> t[1 << 24];
 int mask;
 VISUKITUPIE() { mask = (1 << 24); for (int i = 0; i < (1 << 24); i++) t[i] = mp(-1, 0); }

 void up(int v, int val){
  int save = v;
  v = (v * (ll)v + 3 * v + 7) & (mask - 1);
  while(1){
   if (t[v].x == -1){
    t[v].x = save;
    t[v].y = val;
    return;
   }
   if (t[v].x == save){
    t[v].y += val;
    return;
   }
   v++;
   if (v == mask) v = 0;
  }
 }

 int get(int v){
  int save = v;
  v = (v * (ll)v + 3 * v + 7) & (mask - 1);
  while(1){
   if (t[v].x == -1){
    return 0;
   }
   if (t[v].x == save){
    return t[v].y;
   }
   v++;
   if (v == mask) v = 0;
  }
 }
} ma;

ll ans[200007];
int n;
vector<int> g[200007];
int color[200007];


bool del[200007];
int w[200007];

ll sum[200007];
ll sum2[200007];

void dfsW(int v, int pred){
    w[v] = 1;
    sum[v] = 0;
    sum2[v] = 0;
    for (auto to : g[v]) if (!del[to] && to != pred){
        dfsW(to, v);
        w[v] += w[to];
    }
}

int findVert(int v, int pred, int ww){
    for (auto to : g[v]) if (!del[to] && to != pred && w[to] > ww / 2) return findVert(to, v, ww);
    return v;
}

//unordered_map<int, int> ma;
//int ma[1 << 26];

void dfsFunc(int v, int pred, int mask, int zn){
 mask ^= (1 << color[v]);
 ma.up(mask, zn);
 for (int to : g[v]) if (!del[to] && to != pred) dfsFunc(to, v, mask, zn);
}

void dfsCalcAns(int v, int pred, int mask){
 mask ^= (1 << color[v]);
 sum[v] += ma.get(mask);
 if (mask == 0) sum2[v]++;
 for (int i = 0; i < 26; i++){
  if (mask == (1 << i)) sum2[v]++;
  sum[v] += ma.get(mask ^ (1 << i));
 }
 for (int to : g[v]) if (!del[to] && to != pred) dfsCalcAns(to, v, mask);
}

void dfsCalcSum(int v, int pred){
 for (int to : g[v]) if (!del[to] && to != pred){
  dfsCalcSum(to, v);
  sum[v] += sum[to];
  sum2[v] += sum2[to];
 }
 if (pred == -1) ans[v] += sum[v] / 2 + sum2[v];
 else ans[v] += sum[v] + sum2[v];
}

void build(int v){
    dfsW(v, -1);
    int cc = findVert(v, -1, w[v]);

    for (int to : g[cc]) if (!del[to]) dfsFunc(to, cc, 0, 1);

    for (int to : g[cc]) if (!del[to]){
     dfsFunc(to, cc, 0, -1);
     dfsCalcAns(to, cc, (1 << color[cc]));
     dfsFunc(to, cc, 0, 1);
    }

    dfsCalcSum(cc, -1);

    for (int to : g[cc]) if (!del[to]){
     dfsFunc(to, cc, 0, -1);
    }

    //cout << ccc << endl;

    //cout << cc << endl;

    del[cc] = 1;
    for (auto to : g[cc]) if (!del[to]) build(to);
}

