#include <iostream>
using namespace std;


class LazySegmentTree
{
private:
    int n;
    int *tree, *lazy, *mn;


    void push(const int &i, const int &l, const int &r)
    {
        if (lazy[i] == 0)
            return;
        tree[i] = lazy[i];
        mn[i] = lazy[i];
        if (l != r)
        {
            lazy[i << 1] = lazy[i];
            lazy[i << 1 | 1] = lazy[i];
        }
        lazy[i] = 0;
    }


    void update(const int &i, const int &l, const int &r, const int &tl, const int &tr, const int &v)
    {
        push(i, l, r);
        if (l > tr || r < tl)
            return;
        if (l >= tl && r <= tr)
        {
            lazy[i] = v;
            push(i, l, r);
            return;
        }
        int m = (l + r) >> 1;
        update(i << 1, l, m, tl, tr, v);
        update(i << 1 | 1, m + 1, r, tl, tr, v);
        mn[i] = min(mn[i << 1], mn[i << 1 | 1]);
    }


    int query(const int &i, const int &l, const int &r, const int &tl, const int &tr)
    {
        push(i, l, r);
        if (l > tr || r < tl)
            return 0;
        if (l >= tl && r <= tr)
            return tree[i];
        int m = (l + r) >> 1;
        return query(i << 1, l, m, tl, tr) + query(i << 1 | 1, m + 1, r, tl, tr);
    }


public:
    LazySegmentTree(const size_t &sz)
    {
        n = sz;
        tree = new int[4 * n]{0};
        lazy = new int[4 * n]{0};
        mn = new int[4 * n]{0};
    }


    void upd(const int &l, const int &r, const int &v)
    {
        update(1, 1, n, l, r, v);
    }


    int qry(const int &l, const int &r)
    {
        return query(1, 1, n, l, r);
    }


    int getMin()
    {
        return mn[1];
    }


    void print()
    {
        for (int i = 1; i <= n; ++i)
            cout << qry(i, i) << " \n"[i == n];
    }
};


int main()
{
    ios_base::sync_with_stdio(0);
    cout.tie(0);
    cin.tie(0);


    int r, c, m;
    cin >> r >> c >> m;
    size_t sz = r * c;
    LazySegmentTree lst(sz);
    while (m--)
    {
        int t;
        cin >> t;
        switch (t)
        {
        case 1:
        {
            int x, y, v;
            cin >> x >> y >> v;
            lst.upd((x - 1) * c + y, (x - 1) * c + y, v);
            break;
        }
        case 2:
        {
            int x, v;
            cin >> x >> v;
            lst.upd((x - 1) * c + 1, x * c, v);
            break;
        }
        case 3:
        {
            int x, y;
            cin >> x >> y;
            cout << lst.qry((x - 1) * c + y, (x - 1) * c + y) << "\n";
            break;
        }
        case 4:
        {
            cout << lst.getMin() << "\n";
            break;
        }
        }
    }
}

