struct circle {
 int x, y, r;
} a, b, c;

bool inside(circle a, circle b) {
 if (a.r < b.r) {
  swap(a, b);
 }
 return (a.x - b.x)*(a.x - b.x) + (a.y - b.y)*(a.y - b.y) <= (a.r - b.r)*(a.r - b.r);
}

bool intersect(circle a, circle b) {
 return (a.x - b.x)*(a.x - b.x) + (a.y - b.y)*(a.y - b.y) < (a.r + b.r)*(a.r + b.r)
  && !inside(a, b);
}

