int a[1000008],b[1000008],n,t;
void as(int n)
{
    REW(a,0);
    a[0]=a[1]=1;
    t=0;
    FOR(i,2,n)
    {
        if(!a[i])
        {
            b[++t]=i;
        }
        FOR(j,1,t)
        {
            if(1ll*i*b[j]>n)  break;
            a[b[j]*i]=1;
            if(i%b[j]==0)  break;
        }
    }
}
int main()
{
    cin.tie(0);
    si(n);
    as(n);
    int p,s=0;
    FOL(i,n,0)
    {
        if(!a[i])
        {
           if(n%i==0)
           {
               p=i;
               break;
           }
        }
    }
    s=inf;
    FOL(i,n,n-p+1)
    {
        FOL(j,i-1,1)
        {
            if(!a[j]&&i%j==0)
            {
                s=min(s,i-j+1);
                break;
            }
