vector<int> ms(vector<int>v,int x,int y,int x1,int y1)
{
    if(v.size()<=2||x1==x||y1==y){
            return v;
    }
    int mid=(x+x1)/2;
    int mid1=(y+y1)/2;
    temp.clear();
    for(int f=0;f<v.size();f++)
        if(arr[0][v[f]]>=x&&arr[0][v[f]]<=mid&&arr[1][v[f]]>=y&&arr[1][v[f]]<=mid1)
            temp.push_back(v[f]);
    a=ms(temp,x,y,mid,mid1);
    temp.clear();
    for(int f=0;f<v.size();f++)
        if(arr[0][v[f]]<=x1&&arr[0][v[f]]>mid&&arr[1][v[f]]>=y&&arr[1][v[f]]<=mid1)
            temp.push_back(v[f]);
    b=ms(temp,mid+1,y,x1,mid1);
    temp.clear();
    for(int f=0;f<v.size();f++)
        if(arr[0][v[f]]>=x&&arr[0][v[f]]<=mid&&arr[1][v[f]]<=y1&&arr[1][v[f]]>mid1)
            temp.push_back(v[f]);
    c=ms(temp,x,mid+1,mid,y1);
    temp.clear();
    for(int f=0;f<v.size();f++)
        if(arr[0][v[f]]<=x1&&arr[0][v[f]]>mid&&arr[1][v[f]]<=y1&&arr[1][v[f]]>mid1)
            temp.push_back(v[f]);
    d=ms(temp,mid+1,mid1+1,x1,y1);
    temp.clear();
    ret.clear();
    for(int f=0;f<a.size();f++)
        ret.push_back(a[f]);
    for(int f=0;f<b.size();f++)
        ret.push_back(b[f]);
    for(int f=0;f<c.size();f++)
        ret.push_back(c[f]);
    for(int f=0;f<d.size();f++)
        ret.push_back(d[f]);
    return ret;
}

