#define maxn 300007
char str[maxn];
int dp[maxn][37];
struct node
{
    int to;
    int next;
} edge[maxn];
int head[maxn],tot;
int n,m;
void addedge(int u,int v)
{
    edge[tot].to = v;
    edge[tot].next = head[u];
    head[u] = tot++;
}
int dfs(int no,int ch)
{
    if(dp[no][ch]==-1)
    {
        cout<<"-1"<<endl;
        exit(0);
    }
    else if(dp[no][ch]!=0)return dp[no][ch];
    dp[no][ch]=-1;
    int tmp=0;
    for(int i=head[no]; i!=-1; i=edge[i].next)
    {
        int to=edge[i].to;
        tmp=max(tmp,dfs(to,ch));
    }
    tmp+=(str[no-1]-'a'==ch);
    return dp[no][ch]=tmp;
}
int main()
{
    IO;
    cin>>n>>m>>str;
    memset(head,-1,sizeof head);
    memset(dp,0,sizeof(dp));
    tot = 0;
    for(int i=0; i<m; i++)
    {
        int u,v;
        cin>>u>>v;
        addedge(u,v);
    }
    int ans=0;
    for(int i=1;i<=m;i++)
        for(int j=0;j<26;j++)
            ans=max(ans,dfs(i,j));
    cout<<ans<<endl;
    return 0;
}

