                {
                    d[ux][uy] = d[tx][ty] ;
                    //cout<<tx<<" "<<ty<<" "<<ux<<" "<<uy<<" "<<d[ux][uy]<<endl;
                    q.push(ux*m+uy);
                }
            }
        }
    }
}
