void update(int u, long long v)
{
    //cout << u << " " << v << endl;
    while( u < N)
    {
        bit[u] += v;
        u += ( u & (-u) );
    }
}

long long get(int u)
{
    //cout << u << endl;
    long long ans = 0;
    while( u )
    {
        ans += bit[u];
        u -= ( u & (-u) );
    }
    return ans;
}



