int n;

inline int f(int l, int r, int t) {
  return p2[t]-p2[l-1] + s1[t+1] - s1[r+1];
}
void solve() { 
  cin >> n;
  for (int i = 1; i <= n; ++i) {
    cin >> a[i];
    p1[i] = p1[i-1];
    p2[i] = p2[i-1];
    if (a[i] == 1) {
      p1[i]++;
    }
    if (a[i] == 2) {
      p2[i]++;
    }
  }
  for (int i = n; i > 0; --i) {
    s1[i] = s1[i+1];
    s2[i] = s2[i+1];
    if (a[i] == 1) {
      s1[i]++;
    }
    if (a[i] == 2) {
      s2[i]++;
    }
  }

  int ans = 0;

  for (int i = 0; i <= n; ++i) {
    int t = i;
    for (int j = i+1; j <= n; ++j) {
      while (t < j && f(i, j, t) <= f(i, j, t+1)) t++;
      ans = max(ans, p1[i-1] + s2[j+1] + f(i, j, t));
    }
  }
  cout << ans << endl;
}

int main() {
  ll t = 1;
//  cin >> t;
  while (t--) {
    solve();
  }
  return 0;
}

