// Directions: west, south, east, north (S indices 1-4)
// Parent directions encoded as colors: 2 (W), 3 (S), 4 (E), 5 (N)
// Start cell color: 6

const int COLOR_START = 6;
const int COLOR_PATH = 1;

void program_pulibot() {
    // BFS phase: set parent directions
    vector<array<int, 5>> states;

    // For cells that are not visited (current color 0)
    for (int curr_dir = 0; curr_dir < 4; ++curr_dir) {
        for (int west_state : {-2, -1, 0, COLOR_START, 2,3,4,5}) {
            for (int south_state : {-2, -1, 0, COLOR_START, 2,3,4,5}) {
                for (int east_state : {-2, -1, 0, COLOR_START, 2,3,4,5}) {
                    for (int north_state : {-2, -1, 0, COLOR_START, 2,3,4,5}) {
                        int current = 0;
                        array<int,5> S = {current, west_state, south_state, east_state, north_state};

                        // Check if any adjacent is parent direction (2-5)
                        // Parent direction is determined by the first adjacent cell with color 2-5
                        int parent_dir = -1;
                        if (west_state >= 2 && west_state <=5) parent_dir = 0;
                        else if (south_state >=2 && south_state <=5) parent_dir = 1;
                        else if (east_state >=2 && east_state <=5) parent_dir = 2;
                        else if (north_state >=2 && north_state <=5) parent_dir = 3;

                        if (parent_dir == -1) {
                            // If current cell is the start (color 6)
                            if (current == COLOR_START) {
                                // Explore adjacent cells in some order (E, S, W, N)
                                // Check east
                                if (east_state == 0) {
                                    set_instruction(S, 4, 'E'); // mark parent E (W)
                                    continue;
                                }
                                // Check south
                                if (south_state == 0) {
                                    set_instruction(S, 3, 'S'); // mark parent S (N)
                                    continue;
                                }
                                // Check west
                                if (west_state == 0) {
                                    set_instruction(S, 2, 'W'); // mark parent W (E)
                                    continue;
                                }
                                // Check north
                                if (north_state == 0) {
                                    set_instruction(S, 5, 'N'); // mark parent N (S)
                                    continue;
                                }
                                // No more moves, terminate?
                                set_instruction(S, COLOR_START, 'H');
                            } else {
                                // Not the start and no parent, so invalid
                                continue;
                            }
                        } else {
                            // Current cell is being visited via parent_dir (0: W, 1: S, 2: E, 3: N)
                            // Mark current cell with parent's direction (2: parent W, 3: parent S, 4: parent E, 5: parent N)
                            int parent_color = 2 + parent_dir; // 2,3,4,5 for W,S,E,N parents
                            char move_dir = 'H';
                            // Check adjacent cells in fixed order: W, S, E, N
                            if (S[1] == 0) { // west is 0
                                move_dir = 'W';
                                set_instruction(S, 2, move_dir);
                            } else if (S[2] == 0) { // south
                                move_dir = 'S';
                                set_instruction(S, 3, move_dir);
                            } else if (S[3] == 0) { // east
                                move_dir = 'E';
                                set_instruction(S, 4, move_dir);
                            } else if (S[4] == 0) { // north
                                move_dir = 'N';
                                set_instruction(S, 5, move_dir);
                            } else {
                                // No adjacent cells to explore, backtrack to parent
                                int parent_move = -1;
                                switch (parent_dir) {
                                    case 0: parent_move = 'E'; break; // came from W, move E to backtrack
                                    case 1: parent_move = 'N'; break; // came from S, move N
                                    case 2: parent_move = 'W'; break; // came from E, move W
                                    case 3: parent_move = 'S'; break; // came from N, move S
                                }
                                set_instruction(S, parent_color, parent_move);
                            }
                        }
                    }
                }
            }
        }
    }

    // Backtrack phase: from destination to start, mark path as 1
    for (int curr_color : {2,3,4,5}) {
        for (int west_state : {-2, -1, 0, COLOR_START, 2,3,4,5, COLOR_PATH}) {
            for (int south_state : {-2, -1, 0, COLOR_START, 2,3,4,5, COLOR_PATH}) {
                for (int east_state : {-2, -1, 0, COLOR_START, 2,3,4,5, COLOR_PATH}) {
                    for (int north_state : {-2, -1, 0, COLOR_START, 2,3,4,5, COLOR_PATH}) {
                        array<int,5> S = {curr_color, west_state, south_state, east_state, north_state};
                        int parent_dir = curr_color - 2;
                        char move_dir;
                        switch (parent_dir) {
                            case 0: move_dir = 'E'; break; // parent W, move E
                            case 1: move_dir = 'N'; break; // parent S, move N
                            case 2: move_dir = 'W'; break; // parent E, move W
                            case 3: move_dir = 'S'; break; // parent N, move S
                            default: continue;
                        }
                        set_instruction(S, COLOR_PATH, move_dir);
                    }
                }
            }
        }
    }

    // Termination at start cell (color 6)
    array<int,5> start_S = {COLOR_START, -2, -1, 0, -2};
    set_instruction(start_S, COLOR_PATH, 'T');

    // Handle cases where the path is already at the destination
    array<int,5> dest_S = {COLOR_PATH, -2, -1, -2, -2};
    set_instruction(dest_S, COLOR_PATH, 'T');
}

